import { React, useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import logo from '../../assets/images/how-it-work-thumb.png'
import axios from 'axios'

function Admin() {
  const [email, setEmail] = useState()
  const [addHostelDetails, setAddHostelDetails] = useState({
    name: '',
    address: '',
    details: '',
  })
  const [hostelPicture, setHostelPicture] = useState('')

  const handleSubmit = async (e) => {
    e.preventDefault()
    console.log(hostelPicture)
    const addForm = new FormData()
    addForm.append('data', JSON.stringify(addHostelDetails))
    addForm.append('file', hostelPicture)

    const checkingFormUpdates = await axios.post(
      'https://futa-hostels-10467.herokuapp.com/addHostelDetails',
      addForm,
    )
  }

  const handleChange = (e) => {
    const addNewData = { ...addHostelDetails }
    addNewData[e.target.id] = e.target.value
    setAddHostelDetails(addNewData)
    //console.log(addHostelDetails)
  }
  const addHostelPicture = (e) => {
    let name = e.target.files[0]
    setHostelPicture(name)
    //    console.log(hostelPicture)
  }

  useEffect(async () => {
    const token = localStorage.getItem('accessToken')
    if (!token) console.log('Unable to get token')
    await axios
      .get('/api/read/profile', {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      })
      .then((result) => {
        setEmail(result.data)
        console.log(result)
      })
      .catch((error) => {
        window.location.assign('http://localhost:3000/login')
        console.log('An error has occured and data is not received', error)
      })
  }, [])
  return (
    <>
      <section
        className="appie-hero-area"
        style={{ marginTop: '-100px', marginBottom: '-80px' }}
      >
        <div className="row">
          <div className="col-sm-12 col-md-4 col-lg-4 p-4">
            <div style={{ width: '100%' }}>
              <div
                className="min-vh-100 d-flex flex-column opacity-mask"
                style={{ backgroundColor: 'rgba(0, 0, 0, 0.05)' }}
              >
                <i className="m-3">{email}</i>
                <div
                  className=" bg-white px-5 py-3"
                  style={{
                    width: '90%',
                    alignSelf: 'center',
                    boxShadow: '5px 5px 45px 5px lightgrey',
                    alignItems: 'right',
                  }}
                >
                  <form
                    onSubmit={(e) => {
                      handleSubmit(e)
                    }}
                  >
                    <div className="form-group">
                      <input
                        required
                        onChange={(e) => {
                          handleChange(e)
                        }}
                        className="form-control"
                        type="text"
                        placeholder="Gadget Name"
                        id="name"
                      />
                    </div>
                    <div className="form-group">
                      <input
                        required
                        onChange={(e) => {
                          handleChange(e)
                        }}
                        className="form-control"
                        type="text"
                        placeholder="Address"
                        id="address"
                      />
                    </div>
                    <div className="form-group">
                      <textarea
                        onChange={(e) => {
                          handleChange(e)
                        }}
                        className="form-control"
                        required
                        placeholder="Gadget Issues"
                        style={{
                          height: '150px',
                          borderRadius: '10px',
                        }}
                        id="details"
                      />
                    </div>

                    <div className="form-group">
                      <p>Upload Image</p>
                    </div>
                    <div className="form-group">
                      <input
                        required
                        onChange={(e) => {
                          addHostelPicture(e)
                        }}
                        type="file"
                        id="file"
                        className="form-control"
                        name="file"
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="submit"
                        value="submit"
                        className="form-control bg-primary"
                        style={{ color: 'white' }}
                      />
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
          <div className="col-sm-12 col-md-8 col-lg-8">
            <h1>Details are posted here</h1>
          </div>
        </div>
      </section>
    </>
  )
}

export default Admin
