import React, { useState } from 'react'
import axios from 'axios'
function SponserHomeEight() {
  const [data, setData] = useState({
    username: '',
    email: '',
    password: '',
  })
  const onHandle = (e) => {
    const details = { ...data }
    details[e.target.id] = e.target.value
    setData(details)
  }
  const submitHandle = async (e) => {
    e.preventDefault()
    const result = await axios.post('/api/auth/signup', { data })
    console.log(result)
    // if (result.data === "exist") {
    //   setColor("blue");
    //   setError("User already exist");
    //   console.log(result.data);
    // }else if(result.data === "success") {
    //     sessionStorage.setItem('username',data.username)
    //   window.location.assign("dashboard");
    // } else {
    //   setColor("red");
    //   setError("Invalid details");
    // }
  }
  return (
    <>
      <section className="appie-about-8-area pt-100 pb-100">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <div className="appie-signup-box">
                <h3 className="title">Get Started.</h3>
                <form
                  onSubmit={(e) => {
                    submitHandle(e)
                  }}
                >
                  <div className="input-box">
                    <input
                      onChange={(e) => onHandle(e)}
                      id="username"
                      type="text"
                      placeholder="Username"
                    />
                  </div>
                  <div className="input-box">
                    <input
                      onChange={(e) => onHandle(e)}
                      id="email"
                      type="email"
                      placeholder="Email"
                    />
                  </div>
                  <div className="input-box">
                    <input
                      onChange={(e) => onHandle(e)}
                      id="password"
                      type="password"
                      placeholder="Password"
                    />
                  </div>
                  <div className="input-box">
                    <button type="submit">Sign Up</button>
                  </div>
                  <div className="appie_checkbox_common checkbox_style2">
                    <div>
                      <input type="checkbox" name="checkbox2" id="checkbox4" />
                      <label htmlFor="checkbox4">
                        <span></span>By signing up you agree to our
                        <a href="#">Terms & Conditions.</a>
                      </label>
                    </div>
                    <div style={{ fontSize: '10px', marginBottom: '0px' }}>
                      <center>
                        <i style={{ marginBottom: '-1px', color: 'red' }}>
                          {error}
                        </i>
                      </center>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  )
}

export default SponserHomeEight
